    </br>
   { courses?.map((crs)=> <Card style={{ width: '18rem' }}>
  <CardImg variant="top" src="assets\tech.jpg" />
  <CardBody>
    <CardTitle>{crs.title}</CardTitle>
    <CardText>
    {crs.description}
    </CardText>
  </CardBody>
  <ListGroup className="list-group-flush">
    <ListGroupItem>CourseId{crs.id}</ListGroupItem>
    <ListGroupItem>{crs.facultyName}</ListGroupItem>
    <ListGroupItem>{crs.price}</ListGroupItem>
    <ListGroupItem>{crs.nooflectures}</ListGroupItem>
    <ListGroupItem>{crs.duration}</ListGroupItem>
  </ListGroup>
  <CardBody>
    <CardLink href="#">Course Link</CardLink>
    {/*<CardLink href="#">Another Link</CardLink>*/}
  </CardBody>
</Card>)}


   

</div>

    );
}
